import { useEffect } from 'react';
import { useParallax } from 'react-scroll-parallax';

import CarouselFade from '../../components/carousel/carousel.component';

import rec from '../../assets/rec.png';

import './home.styles.scss';

const Home = () => {
  useEffect(() => {
    const handleScroll = (e) => {
      if (
        document.querySelector('').scrollTop > 80 ||
        document.documentElement.scrollTop > 80
      ) {
        console.log(document.body.scrollTop);
        document.querySelector('.navbar').style.padding = '30px 10px';
        document.querySelector('.navbar-brand').style.width = '20%';
      }
    };
    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div className='home-container'>
      <CarouselFade />
      <div className='main-container'>
        <h1>SHARE WHAT YOU DO</h1>
        <hr />
        <p>
          Lorem ipsum dolor sit amet consectetur adipisicing elit. Eveniet
          consequatur laborum et voluptates, praesentium incidunt odit in dolor
          voluptas repudiandae, facilis ipsa dicta reprehenderit? Magni aut
          minima repellendus ea consequatur.
        </p>
        <div className='icon-set'>
          <img src={rec} alt='' />
          <p>
            Lorem ipsum dolor sit amet consectetur adipisicing elit. Optio quos
            animi, eaque aperiam saepe dolores consequatur recusandae dolorum.
            Reprehenderit culpa ipsam quia, impedit qui quidem placeat dolorem
            repellendus ducimus laborum.
          </p>
        </div>
      </div>
      <div className='parallax1'></div>
      <div className='reviews'></div>
    </div>
  );
};

export default Home;
